extern crate smtp;

use smtp::client::Client;
use std::net::{TcpStream, ToSocketAddrs};
use std::io::{self, Write};

fn send_email(to: &str, subject: &str, body: &str) -> Result<(), Box<dyn std::error::Error>> {
    let stream = TcpStream::connect(("smtp.gmail.com", 587))?;
    let mut client = Client::new(stream)?;

    // Start TLS
    client.starttls()?;

    // Login to the SMTP server
    client.login("your-email@gmail.com", "your-password")?;

    // Compose the email
    let from = "your-email@gmail.com";
    let headers = format!("From: {}\r\nTo: {}\r\nSubject: {}\r\n\r\n",
                          from, to, subject);
    let message = format!("{}\r\n{}", headers, body);

    // Send the email
