class CoinManager: NSObject, ObservableObject {
    @Published var coinCount = 0

    override init() {
        super.init()
        checkPurchaseStatus()
    }

    func buyCoins() {
        let productID: String = "com.example.app.coins100"

        if let product = getProduct(productID) {
            let payment = SKPayment(product: product)
            SKPaymentQueue.default().add(self)
            SKPaymentQueue.default().add(payment)
        } else {
            print("Product not found")
        }
    }

    func useCoin() {
        if coinCount > 0 {
            coinCount -= 1
        } else {
            print("Not enough coins")
        }
    }

    func checkPurchaseStatus() {
        let request = SKProductsRequest(productIdentifiers: Set<String>(["com.example.app.coins100"]))
        request.delegate = self
        request.start()
    }
}

extension CoinManager: SKProductsRequestDelegate {
    func productsRequest(_ request: SKProductsRequest, didReceive response: SKProductsResponse) {
        if response.products.count > 0 {
            let product = response.products[0]
            if product.productIdentifier == "com.example.app.coins100" {
                coinCount = product.price.intValue
            }
        }
    }
}

extension CoinManager: SKPaymentTransactionObserver {
    func paymentQueue(_ queue: SKPaymentQueue, updatedTransactions transactions: [SKPaymentTransaction]) {
        for transaction in transactions {
            switch transaction.transactionState {
            case .purchased:
                SKPaymentQueue.default().finishTransaction(transaction)
                coinCount += 100
            case .failed:
                SKPaymentQueue.default().finishTransaction(transaction)
                print("Purchase failed")
            default:
                break
            }
        }
    }
}
