use mail::message::{MessageBuilder, Part};
use reqwest;
use serde::{Deserialize, Serialize};

#[derive(Serialize, Deserialize)]
struct ChatGptResponse {
    thought: String,
}

async fn get_chatgpt_response(prompt: &str) -> Result<String, Box<dyn std::error::Error>> {
    let resp = reqwest::get(format!("https://api.anthropic.com/v1/assistant?prompt={}", prompt))
        .await?
        .text()
        .await?;
    let response: ChatGptResponse = serde_json::from_str(&resp)?;
    Ok(response.thought)
}

