#[tokio::main]
async fn main() -> Result<(), Box<dyn std::error::Error>> {
    let recipients = vec![
        ("example1@example.com", "John Doe", "CEO of ABC Corp"),
        ("example2@example.com", "Jane Smith", "Professor at XYZ University"),
