use csv::Reader;
use std::fs;

fn load_recipients(file_path: &str) -> Vec<Recipient> {
    let csv_data = fs::read_to_string(file_path).expect("Failed to read file");
    let mut reader = Reader::from_reader(csv_data.as_bytes());
    reader
        .records()
        .filter_map(|result| result.ok())
        .map(|record| {
            let name = record.get(0).unwrap().to_string();
            let email = record.get(1).unwrap().to_string();
            let job_title = record.get(2).map(|title| title.to_string());
            Recipient { name, email, job_title }
        })
        .collect()
}
```
Next, let's create a function to render the email template using Handlebars:
```rust
use handlebars::Handlebars;

fn render_email_template(template: &str, recipient: &Recipient) -> String {
    let mut handlebars = Handlebars::new();
    handlebars.register_escape_fn(|s| s.escape_default());
    handlebars.set_strict_mode(true);
    handlebars.register_helper("name", Box::new(|h: &handlebars::Helper, r: &mut handlebars::RenderContext| {
        let value = h.value(&r.context()).unwrap_or_else(|| "".into());
        Ok(handlebars::Value::String(format!("Dear {},", value)))
    }));
    handlebars.render_template(template, recipient).unwrap_or_else(|e| e.to_string())
}
```
