```rust
use lettre::{SmtpTransport, Transport};
use lettre::transport::smtp::authentication::Credentials;
use lettre::message::MailBuilder;

fn send_email(email: &str, subject: &str, body: &str) {
    let transport = SmtpTransport::new("smtp.gmail.com:587")
        .credentials(Credentials::new(
            "your-email@gmail.com".into(),
            "your-password".into(),
        ))
        .build();

    let message = MailBuilder::new()
        .from("your-email@gmail.com".into())
        .to(email.into())
        .subject(subject)
        .body(body.into())
        .unwrap()
        .build()
        .unwrap();

    transport.send(message).unwrap();
}
```
